import unittest
from collections import Counter

class Test(unittest.TestCase):
    def test1(self):
        self.assertEquals(
                solve(""),
                983
        )

def solve(s):
    counter = Counter(s)
    result = 0
    single_letter = 0

    for k,v in counter.items():
        if v % 2 == 0:
            result += v
        else:
            result += v - 1
            single_letter = 1

    return result + single_letter

if __name__ == "__main__":
    unittest.main()
